--
-- Triggers `OrderItems`
--
DELIMITER $$
CREATE TRIGGER `updateTotalPrice` AFTER UPDATE ON `OrderItems` FOR EACH ROW BEGIN
    UPDATE Orders
    SET TotalPrice = (
        SELECT SUM(Count * Price)
        FROM OrderItems
        WHERE Orders.OrderId = OrderItems.OrderId
        GROUP BY OrderItems.OrderId
    )
    WHERE Orders.OrderId = NEW.OrderId;
END
$$
DELIMITER ;
DELIMITER $$
CREATE TRIGGER `updateTotalPriceOnInsert` AFTER INSERT ON `OrderItems` FOR EACH ROW BEGIN
    UPDATE Orders
    SET TotalPrice = (
        SELECT SUM(Count * Price)
        FROM OrderItems
        WHERE Orders.OrderId = OrderItems.OrderId
        GROUP BY OrderItems.OrderId
    )
    WHERE Orders.OrderId = NEW.OrderId;
END
$$
DELIMITER ;

