INSERT INTO `Products` (`ProductId`, `CategoryId`, `Name`, `Type`, `Color`, `SugarContentId`, `BrandId`, `Volume`, `Strength`, `Taste`, `GrapeVarietyId`, `Aging`, `Description`, `Count`, `Price`, `Image`, `Visibility`) VALUES
(1, 1, 'Jameson Irish Whiskey', 'Бленд', 'Золотисто-бурштиновий', NULL, 1, 0.7, 40, 'Пряний, фруктовий', NULL, 6, '<p>Кожна пляшка ірландського віскі — <strong>Jameson</strong> це понад 220 років традицій і майстерності. Джон Джемесон, талановитий підприємець і тонкий знавець віскі, започаткував свою віскікурню в 1780 році в самому серці Дубліна. Родовий девіз Sine Metu («Без страху») допомагає пояснити унікальний новаторський підхід Джона Джемесона, завдяки якому він створив найм\'якіше віскі у світі. Незабаром після заснування віскікурня <i>John Jameson&amp;Son</i> стала одним із найбільших підприємств Ірландії. Джон Джемесон не шкодував сил і коштів для того, щоб забезпечити безперебійне постачання чудової сировини та гарантувати бездоганну якість свого віскі. Слава про чудовий смак віскі <strong>Jameson</strong> поширилася всією планетою, завдяки чому до кінця XIX століття <strong>Jameson</strong> став ірландським віскі номер один у всьому світі. Першість <strong>Jameson</strong> у цій категорії й тепер не піддається сумніву. Серед шанувальників <strong>Jameson</strong> — знаменитості минулого та сьогодення: королева Єлизавета I та Джеймс Джойс, Софія Форд Коппола, Пірс Броснан і багато інших.</p><p>Основними компонентами ірландського віскі <strong>Jameson</strong> є ячмінь, ячмінний солод і найчистіша джерельна вода. В Ірландії солод висушують у закритих печах, тому тутешнє віскі не має запаху диму, — його відрізняє легкий квітковий аромат. Крім цього, ірландське віскі — єдине віскі у світі, яке піддається потрійній дистиляції й тому має м\'якший смак, ніж його «побратими» із Шотландії та Америки. Отримане віскі розливається в дубові бочки, де до цього неодмінно витримували херес. У цих бочках у повній темряві та тиші віскі визріває, набираючись танінів, «дорослішає» впродовж мінімум шести років. За цей час остаточно формується букет і смак продукту, напій набуває золотистого кольору. Як кажуть ірландці, Святий Патрік пускає сльозу подяки в кожну бочку.</p><p>Характер кожного сорту ірландського віскі визначає майстер купажу — він з\'єднує в одній бочці до сорока різних сортів. Саме це складне поєднання різних віскі, кожне з яких унікальне, створює воістину магічний смак <strong>Jameson</strong>.</p><p>На етикетці віскі <strong>Jameson</strong> значиться герб Джона Джемесона та незмінний девіз його сім\'ї: «Sine metu» («Без страху»). Бурштиновий <strong>Jameson</strong> надає впевненості, комфорту й вміння отримувати задоволення від життя.</p>', 35, 599, '6558cb0bdb0f7.jpg', 0),
(2, 1, 'Jim Beam White', 'Бурбон', 'Золотисто-бурштиновий', NULL, 2, 1, 40, 'Солодкий', NULL, 4, '<p>Історія компанії <strong>Jim Beam</strong>, популярної в усьому світі, пов\'язана з ім\'ям Джейкоба Біма, який наприкінці 18 століття почав виробляти віскі з розмелених кукурудзяних зерен, жита та солоду. Цей міцний спиртний напій бурштинового кольору отримав назву бурбон за назвою округу Бурбон у штаті Кентуккі, де його вперше виготовили. Джейкоб Бім продав першу бочку свого бурбону 1795 року. У наш час історія компанії Jim Beam налічує вже понад 200 років, але її власники й далі дотримуються багатих традицій своїх предків, передаючи рецепт чудового віскі з покоління в покоління.</p><p>\"<strong>Jim Beam</strong>\" — бурбон №1 у світі! Щорічно в усьому світі випивають понад 40 мільйонів літрів цього дивовижного напою. Це справжній віскі, повсюдно визнаний завдяки своїй чудовій якості та веселій вдачі. Не менш як 51% сусла, за прийнятими стандартами, створюють з кукурудзи. \"Джим Бім\" витримують 4 роки у нових обвуглених бочках, зроблених з деревини американського дуба.</p>', 15, 729, '6558cabdba0fd.jpg', 1),
(3, 2, 'Mount of Georgia Alazani Valley червоне напівсолодке', NULL, 'Червоне', 3, 4, 0.75, 12, 'М’який фруктовий з чіткими акцентами темних фруктів', 2, NULL, '<p><strong>Alazani Valley, Medium Sweet Red</strong> ― червоне напівсолодке вино зі спокійним смако-ароматичним букетом темних ягід і фруктів. Виготовлене із незвичайного винограду Сапераві ― справжньої гордості грузинського виноробства. Соковиті грона достигають під лагідним сонцем Алазанської долини з ідеальними умовами для смачного вина. Ягоди збирають лише вручну і дбайливо пресують, щоб не пошкодити кісточку. У смаку присутній приємний баланс м’якості та фруктових ноток з легкою кислотністю. Аромат розкривається відтінками стиглої сливи, вишні і лісової ожини. Таке десертне вино бездоганно смакує з тістечками, шоколадом та літніми фруктами.</p>', 25, 139, '655d0e4fc233c.jpg', 1),
(4, 2, 'VDOMA ординарне столове сухе біле сортове Шардоне Шабо', 'Натуральні вина', 'Світло-жовтий', 1, 3, 0.75, 12.2, 'Свіжий, збалансований, округлий з тривалим посмаком', 3, NULL, '<p>Вино, виготовлене з європейського сорту винограду Шардоне, який ми збираємо вручну. Лози були спеціально підібрані для нашого теруару з найкращих розплідників Італії. На певних етапах виробництва контроль якості забезпечують міжнародні експерти. З гордістю презентуємо вам Шардоне світло-солом\'яного кольору, яке зачаровує ароматом з нотками білого персика, обліпихи та квітковими тонами і має свіжий, збалансований, округлий смак з тривалим післясмаком.</p>', 35, 129, '655d11c23b293.jpg', 1),
(5, 2, 'Casa Clara da Malta біле сухе', 'Натуральні вина', 'Біле', 1, 5, 0.75, 12.5, 'Блискучий колір з відтінками соломи. Легка кислотність, збалансоване тіло', 4, NULL, '<p>Після легкого подрібнення та пресування винограду, попередньо очищеного, чисте сусло бродить під контрольованою температурою бродіння.Вино яскравого солом\'яного кольору з нотками цитрусових і молодим фруктовим ароматом. Має м\'який смак, помірну кислотність з гармонійним тілом і багатим характером.</p>', 34, 129, '655d1388ade49.jpg', 1),
(6, 2, 'VDOMA ординарне столове сухе червоне сортове', 'Натуральні вина', 'Темно-вишневий', 1, 3, 0.75, 12.3, 'Округлий, збалансований з м’якими танінами', 5, NULL, '<p>Вино, виготовлене з французького сорту винограду Каберне Совіньйон, який ми збираємо вручну. Лози були спеціально підібрані для нашого теруару з найкращих розплідників Італії. На певних етапах виробництва контроль якості забезпечують міжнародні експерти. З гордістю презентуємо вам Каберне темно-рубінового кольору, яке зачаровує ароматом з нотками чорної смородини та стиглих слив і має округлий, збалансований смак з м’якими танінами.</p>', 45, 143, '655d150f977a7.jpg', 1),
(7, 2, 'Capela DOC рожеве сухе', 'Натуральні вина', 'Рожеве', 1, 6, 0.75, 12, 'Ніжний лососевий колір. Смак червоних фруктів, м\'якість та свіжість', 5, NULL, '<p>Виноград ретельно відбирають як під час збору, так і на виноробні. Помірна холодна мацерація протягом 2 годин і бродіння при контрольованій температурі 16ºC-18ºC. Витримується на дрібному осаді 1 місяць.Колір лососевий. Аромат червоних фруктів (вишні, полуниці) з квітковими нотами. На смак вино м\'яке з нотками червоних фруктів. Легкий післясмак.</p>', 30, 169, '655d16a1b40a2.jpg', 1),
(8, 3, 'Фраголіно Fiorelli Bianco біле солодке', 'Фраголіно', 'Біле', NULL, 7, 0.75, 7, 'Смак – гармонійний, приємний, фруктовий, з виразними тонами лісової суниці.', 6, NULL, '<p>Завод Тосо – виробництво із більш ніж віковою історією. Це компанія, в якій вдало інтегровані традиції і сучасні технології виробництва. Завод обладнаний найновішим устаткуванням для бродіння, розливу і зберігання вина, з дотриманням звукового, світлового і температурного режимів. Тосо - один із небагатьох заводів Італії, який отримав екологічну сертифікацію Emas.Всі виноградники Тосо зосереджені в гірському регіоні Ланге. Вони ростуть на схилах, культивуються за суворими стандартами, не обробляються шкідливими речовинами. Виноградна лоза – живий організм, який поважають, цінують, люблять та враховують його природний цикл і темп росту. До слова, більшість врожаю збирають вручну.Компанія суворо контролює всі процеси виробництва, умови праці своїх робітників, якість сировини і кінцевої продукції. Саме це дає змогу гарантувати, що напої Тосо – напої найвищої якості.Щорічно напої компанії Тосо отримують нові нагороди на всеможливих світових винних конкурсах, а продукція компанії займає лідируючі позиції по продажах не тільки на території Італії, але й на території всієї Європи.</p>', 45, 169, '655dbfc81879c.jpg', 1),
(9, 3, 'Astoria Butterfly Ribolla Gialla Brut біле брют', 'Ігристе', 'Солом\'яно-жовтий', NULL, 8, 0.75, 12, 'Тонкий і збалансований, з ароматним післясмаком', 7, NULL, '<p><strong>Astoria</strong> – італійська сімейна виноробна компанія. Заснована у 1987 році сім\'єю виноробів, братами Паоло та Джорджо Полегато.<br>Вже три десятиліття<strong> Astoria</strong> з гордістю презентує виняткові ігристі та витримані вина, які створюються у власному виноробному маєтку Astoria Val De Brun.<br>Висока якість і стильний дизайн вин <strong>Astoria</strong> дозволяють з гідністю презентувати їх у найтрендовіших ресторанах, енотеках і барах світу. Це серце відомої винної зони Конельяно-Вальдобб\'ядене з престижним статусом DOCG.<br>Місцевість вважається однією з найсприятливіших виноробних зон Італії завдяки особливому м\'якому клімату.<br>Виноробному підприємству <strong>Astoria</strong> належать сорок акрів місцевих виноградників, розташованих на передальпійських пагорбах із крутими схилами.</p>', 50, 179, '655dc28e62e9a.jpg', 1),
(10, 3, 'Heretat el Padruell Cava біле брют', 'Кава', 'Світло-солом\'яний', 5, 9, 0.75, 11.5, 'Вино має легкий, ніжний, добре збалансований смак з освіжним посмаком.', 8, NULL, '<p>У 1647 році на одному з мальовничих пагорбів регіону Пенедес було засновано маєток El Padruell. Під час війни з Францією, у XVII столітті, цей маєток слугував укриттям для місцевих жителів, а з XVIII століття тут стали висаджувати виноградники та виробляти вино. У 1943 році енолог Жауме Серра Гуель купує маєток Ель Падруель і створює винарню Jaume Serra. винарня розташована в середземноморському прибережному місті Vilanova y la Geltru, приблизно 50&nbsp;км на південь від Барселони. Виробництво вина було розпочате 1947 року. у 1984 році винарня була оновлена, потім у 1997 році знову була модернізована, після того як родина Гарсії Карьона придбала її.</p><p>Традиції виноробства компанія Гарсія Карьон підтримує вже кілька століть. Зараз компанія є одним із найвідоміших виробників вина в Іспанії та п\'ятим за популярністю винним брендом світу. Сьогодні вина Гарсія Каріон продають у 130 країнах на 5 континентах. Завдяки поєднанню традиційних виноградарських методик і сучасних технологій вина Garcia Carrion мають чудову якість і доступну ціну.</p>', 50, 199, '655dc396049bf.jpg', 1),
(11, 3, 'Heretat el Padruell Cava Brut Nature біле брют-натюр', 'Кава', 'Світло-солом\'яний', 5, 9, 0.75, 11.5, 'Добре збалансований, з освіжними яблучно-цитрусовими тонами та елегантним пасмом, з помірною кислотністю', 9, NULL, '<p>Виноробня була заснована в 1943 році видатним енологом Жауме Серра Гуелем. Розташована вона в субрегіоні Penedes, найбільшому районі виноградарства в Каталонії, на узбережжі середземноморського міста Vilanova y la Geltru, на 50&nbsp;км південніше від Барселони.<br><br>Виробництво вина було розпочате 1947 року. У 1984 році виноробня була оновлена, а потім, у 1997 році, після того, як її придбала сім\'я Гарсія Каріон, модернізована.<br><br>Виноробна компанія Garcia Carrion розпочала своє існування в далекому 1890 році. Саме тоді прадід нинішнього власника компанії — Хосе Гарсія-Каріон — створив величезний виноградник, щоб виробляти вина для експорту до Франції. З того часу виноробня розрослася і стала одним із найвідоміших виробників вина в Іспанії та п\'ятим за популярністю винним брендом світу. Сьогодні вина Гарсія Каріон продають у 130 країнах на 5 континентах. Завдяки поєднанню традиційних виноградарських методик і сучасних технологій, вина Garcia Carrion мають чудову якість і доступну ціну.</p>', 50, 199, '655dc4ec311f9.jpg', 1),
(12, 3, 'Montecampo Prosecco біле сухе', 'Просеко', 'Солом\'яно-жовтий', 1, 10, 0.75, 11, 'Приємний, добре збалансований, з легкою нотою мигдалю, типовою для сорту Глера', NULL, NULL, '<p>Сімейне підприємство виноробів <strong>Zonin</strong> має майже двохсотлітню історію. Компанія була заснована в 1821 році та на сьогодні є провідним виробником вина в Італії. Компанією керує вже сьоме покоління родини Зонін.</p><p>Підприємство має 11 виноробних господарств, і їхні виноградники розташовуються на 2000 гектарів землі в Італії, ще 2000 гектарів відведено під заводи та підвали для зберігання вин, а також виноробня має виноградники на східному узбережжі США в містечку Барбурсвіл.<br>Кожне виноробне господарство компанії <strong>Zonin</strong> є автономним виробником. Це гарантує індивідуальність кожної марки, унікальний характер вина, який має зв\'язок із географічним регіоном.</p><p>Контроль якості на підприємстві здійснюється професійними енологами, якими керує відомий винороб із міжнародною репутацією Франко Джакозі.</p><p>Вина <strong>Zonin</strong> представлені на ринках 89 країн світу.</p><p>Девіз компанії <strong>Zonin</strong> стоїть на кожній пляшці: \"Винороби Італії з 1821 року\".</p>', 40, 265, '655dc5be69a6e.jpg', 1),
(13, 3, 'Astoria Prosecco Spumante DOC Rose Millesimato рожеве екстра-сухе', 'Просеко', 'Рожеве', 6, 8, 0.75, 11, 'Характерний, приємно густий і збалансований', 1, NULL, '<p>Astoria — істинно італійська родинна виноробна компанія. Заснована в 1987 році родиною виноробів, братами Паоло і Джорджо Полегато. Уже три десятиліття Astoria з гордістю презентує виняткові ігристі та витримані вина, які створюються у власному виноробному маєтку Astoria «Val De Brun». Висока якість і стильний дизайн вин Astoria дають змогу із гідністю презентувати їх у найбільш трендових ресторанах, енотеках і барах світу.</p><p>Виноробний маєток Astoria «Val De Brun» розташований в Рефронтоло — муніципалітеті регіону Венето провінції Тревізо. Це серце відомої винної зони Конегліано-Вальдобб\'ядене з престижним статусом D.O.C.G. Місцевість вважається однією з найсприятливіших виноробних зон Італії завдяки особливому м\'якому клімату. Виноробному підприємству Astoria належать сорок акрів місцевих виноградників, розташованих на передальпійських пагорбах із крутими схилами.</p>', 45, 222, '655dc656affef.jpg', 1),
(14, 3, 'Astoria Prosecco Treviso Butterfly Extra Dry біле екстра-сухе', 'Просеко', 'Біле', 6, 8, 0.75, 11.5, 'Смак свіжий, чудово збалансований, приємний, гармонійний', 1, NULL, '<p>Astoria — істинно італійська сімейна виноробна компанія. Заснована в 1987 році сім\'єю виноробів, братами Паоло і Джорджо Полегато. Уже три десятиліття Astoria з гордістю презентує виняткові ігристі та витримані вина, які створюються у власному виноробному маєтку Astoria «Val De Brun». Висока якість і стильний дизайн вин Astoria дають змогу із гідністю презентувати їх у найбільш трендових ресторанах, енотеках і барах світу.</p><p>Виноробний маєток Astoria «Val De Brun» знаходиться в Рефронтоло — муніципалітеті регіону Венето провінції Тревізо. Це серце відомої винної зони Конельяно-Вальдобб\'ядене з престижним статусом D.O.C.G. Місцевість вважається однією з найбільш сприятливих виноробних зон в Італії завдяки особливому м\'якому клімату. Виноробному підприємству Astoria належать сорок акрів місцевих виноградників, розташованих на передальпійських пагорбах із крутими схилами.</p>', 50, 222, '655dc69ccfda4.jpg', 1),
(15, 5, 'Captain Morgan Spiced Gold', ' Ромовий напій', 'Святковий, золотий', NULL, 11, 1, 35, 'Приємний, солодкий і м\'який з насиченими відтінками екзотичних фруктів, ванілі та карамелі', NULL, NULL, '<p>\"Скарб Карибського моря\", Captain Morgan Original Spiced Gold, майстерно змішаний з найкращого рому преміум-класу та екзотичних острівних спецій. Наш класичний ром витриманий у добірних бочках з американського білого дуба для отримання глибокого, насиченого смаку з відтінками ванілі, фруктів і коричневого цукру.&nbsp;</p><h3><strong>Історія</strong></h3><p>Ямайка, 1944 рік. Бізнесмен Семюель Бронфман куштує незвичний карибський ром в аптеці Levy Brothers. Серія вмовлянь, формальностей із урядом і секрет надзвичайного напою опиняється в руках Семюеля.</p><p>Сьогодні Captain Morgan є одним із найпопулярніших алкогольних брендів у світі, щодня продається понад 400 000 пляшок.&nbsp;</p><p>Captain Morgan Original Spiced Gold — це ідеальне поєднання ванілі та теплих прянощів із преміальним карибським ромом.</p><p>Його фруктовий аромат, відтінки насиченого дуба та гладкий, збалансований післясмак – улюбленець піратів та сучасних шанувальників в усьому світі.</p><p><strong>АРОМАТ</strong></p><p>Багатий і м\'який з екзотичними тонами ванілі та легкими відтінками меду та мигдалю.</p><p><strong>СМАК</strong></p><p>Приємний, солодкий і м\'який з насиченими відтінками екзотичних фруктів, ванілі та карамелі.&nbsp;</p><h3><strong>Ідеальна подача</strong></h3><p>Captain Morgan Original Spiced Gold завдяки м\'якому та складному смаку прекрасно підійде як лонг-дрінк. Якщо пити його маленькими ковтками, він зможе повністю розкрити чарівні фруктові та пряні відтінки смаку й аромату. Ром ідеальний у поєднанні з колою підійде для створення найоригінальніших коктейлів, надавши їм неповторного колориту. У чистому вигляді найкраще пити Captain Morgan Spiced Gold з додаванням льоду та шматочка лайма. Створюйте власні поєднання або знайдіть натхнення у наших рецептах нижче.&nbsp;</p><h3><strong>Captain &amp; Cola. Класичний коктейль з пікантним смаком.</strong></h3><p><strong>Інгредієнти:</strong></p><ul><li>50 мл ромового напою Captain Morgan Original Spiced Gold</li><li>150 мл коли</li><li>1 скибочка лайма</li></ul><p><strong>Спосіб приготування:</strong></p><p>Візьміть склянку. Ідеально підійде висока склянка, але розмір склянки неважливий. Наповніть склянку кубиками льоду та налийте 50 мл Captain Morgan Original Spiced Gold і 150 мл коли. Добре перемішайте і вичавте трохи лайма для пікантності.&nbsp;</p><h3><strong>Captain &amp; Ginger</strong></h3><p><strong>Інгредієнти:</strong></p><ul><li>50 мл ромового напою Captain Morgan Original Spiced Gold</li><li>150 мл імбирного елю</li><li>лаймовий сік (кількість за бажанням)</li></ul><p><strong>Спосіб приготування:</strong></p><p>Візьміть склянку. Ідеально підійде висока склянка, але розмір склянки неважливий. Наповніть склянку кубиками льоду та налийте 50 мл Captain Morgan Original Spiced Gold і 150 мл імбирного елю. Добре перемішайте і вичавте трохи лайма для пікантності.&nbsp;</p>', 50, 439, '655dc7b31976c.jpg', 1),
(16, 5, 'Angostura Reserva', 'Білий ром', 'Білий', NULL, 12, 1, 37.5, 'З нотками ванілі, ніжних спецій, цитрусових і кокосового молока з тривалим посмаком', NULL, 3, '<p>Історія Будинку Ангостура налічує майже 200 років. 1824 військовий лікар армії Симона Болівара — Йоганн Готліб Бенджамін Зігерт — розпочав виробництво лікарських, ароматичних бітерів за власними секретними рецептурами. На створення рецепта пішли роки, але результат був чудовим. Новий засіб допомагав ефективно боротися з черевним болем і розладами шлунка. Згодом ароматичні бітери Angostura стають улюбленим інгредієнтом для коктейлів з-поміж усіх барменів світу.</p><p>Сьогодні ром і бітери <strong>Angostura</strong> репрезентовані в 140 країнах світу та мають безліч нагород.</p>', 40, 399, '655dc89e4f345.jpg', 1),
(17, 5, 'Angostura 5 Age', 'Витриманий ром', ' Золотистий', NULL, 12, 0.7, 40, 'М\'який, з нотами карамелі, тофі та ванілі. Характеризується нотами тропічних фруктів, прянощів та дуба', NULL, 5, '<p>Компанія Angostura Limited має довгу історію, яка починається з 1824 року. Тоді німецький лікар Йоганн Готліб Бендер відкрив підприємство в місті Ангостура на острові Тринідад і Тобаго, де почав виробляти бітери, які використовувалися для лікування хвороб шлунку та інших розладів. У 1875 році компанія отримала медаль на Всесвітній виставці в Парижі за свій Angostura Aromatic Bitters. З того часу бітери Angostura стали дуже популярні в усьому світі, і їх використовують у багатьох коктейлях, таких як Manhattan та Old Fashioned. У 1947 році компанія Angostura придбала одну з найбільших дистильерій рому на острові Тринідад і Тобаго, що дозволило їй розширити свій асортимент на виробництво рому. Сьогодні Angostura є однією з найбільших компаній, що виробляє ром на Карибських островах, і продає свої продукти в більше ніж 140 країнах світу.</p>', 50, 399, '655dc8f29a105.jpg', 1),
(18, 4, 'Martini Bianco солодкий 1 л 15%', 'Вермут Б\'янко', 'Світло-солом\'яний', 4, 13, 1, 15, 'М\'який, елегантний, за мірою насичений, з приємною солодкістю і пікантними пряними тонами', 10, NULL, '<p>Розроблений 1910 року на основі Ванільного Вермуту, який випускала в той час компанія. На смак вермут є прекрасною солодко-гіркою композицією з помітною ноткою ванілі.</p><p>Цей світло-солом\'яний за кольором вермут є найароматнішим з усіх.</p>', 40, 319, '656d8d540d6e8.jpg', 1),
(19, 4, 'Martini Fiero 0.75 л 15%', NULL, 'Яскраво-червоний', NULL, 13, 0.75, 15, 'Цитрусові нотки', NULL, NULL, '<p><strong>Martini Fiero</strong> — новинка в лінійці вермутів Martini. Рецептура Martini Fiero була спеціально збалансована для кращого поєднання з тоніком. У приготуванні Martini Fiero використовуються тільки 100% натуральні інгредієнти. Смак і аромат Martini Fiero мають насичені цитрусові нотки, завдяки вермуту, що входить у рецептуру, цедрі апельсинів і апельсиновому соку.</p>', 40, 319, '656d8da381fa1.jpg', 1),
(20, 4, 'Martini Extra Dry сухий 1 л 18%', 'Вермут Секко', 'Солом\'яний', 1, 13, 1, 18, 'М\'який, пряний, сухуватий', NULL, NULL, '<p><strong>Пікантний, ароматний, легкий</strong></p><p>1890 року сини Луїджі Россі почали розробляти новий стиль <strong>Martini</strong>. На розроблення в них пішло 10 років і на рубежі століть, на новий 1900 рік, <strong>Martini Extra Dry</strong> був представлений споживачам і відразу ж дістав міжнародного визнання.</p><p>Цей вермут на смак сухий, але не гіркий. Солом\'яного кольору, з більш вираженим, ніж у <i>Rosso</i>, ароматом, у якому є нотки малини, лимона й трохи ірису.</p>', 40, 395, '656d8e728cb72.jpg', 1),
(21, 6, 'Jägermeister 0.7 л 35%', 'Лікер', 'Темний', NULL, NULL, 0.7, 35, 'Насичений, з багатьма відтінками трав і коренів.', NULL, NULL, '<p>Коріння нашого приголомшливого логотипу беруть початок в легенді про святого Губерта. Коли він був мисливцем, йому прийшло бачення могутнього оленя з сяючим хрестом між рогами. Цей образ поміняв його, і надалі він почав більше почитати природу, а в кінцевому підсумку став відомий як покровитель мисливців. Курт Маст вирішив, що ця сильна історія — ідеальна для його напою і вибрав зображення оленя в знак пошани «Майстра-мисливця».</p>', 50, 419, '656d8f3f1ba75.jpg', 1),
(22, 6, 'Jim Beam Apple 4 роки витримки 1 л 32.5%', 'Лікер', 'Золотистий бурштин', NULL, 2, 1, 32.5, 'Нотки яблука, фруктового пирога, карамель', NULL, 4, '<p>Історія компанії <strong>Jim Beam</strong>, широко відомої в усьому світі, пов\'язана з ім\'ям Джейкоба Біма, який наприкінці 18 століття почав виробляти віскі з розмелених кукурудзяних зерен, жита та солоду. Цей міцний спиртний напій бурштинового кольору нарекли бурбоном на честь округу Бурбон у штаті Кентуккі, де він був вперше виготовлений. Джейкоб Бім продав першу бочку свого бурбона 1795 року. У наш час історія компанії <strong>Jim Beam</strong> налічує вже понад 200 років, але власники компанії й до цього часу дотримуються багатих традицій своїх предків, передаючи рецепт чудового віскі з покоління в покоління.</p><p>\"<strong>Jim Beam</strong>\" — бурбон №1 у світі! Щорічно у всьому світі випивається понад 40 мільйонів літрів цього дивовижного напою. Це справжній віскі, який набув повсюдного визнання завдяки своїй чудовій якості та веселій вдачі. Не менш 51% сусла, за прийнятими стандартами, створюється з кукурудзи. Витримують \"Джим Бім\" упродовж 4 років у нових обвуглених бочках, зроблених із деревини американського дуба.</p>', 50, 999, '656d8fce84d6f.jpg', 1),
(23, 7, 'Хортиця Преміум 0.5 л 40%', NULL, 'Прозорий, чистий', NULL, 15, 0.5, 40, 'Горілка преміумкласу, яка має бездоганний смак - неймовірно чистий, свіжий і м\'який', NULL, NULL, '<h3><strong>ХОРТИЦЯ ПРЕМІУМ</strong></h3><p>Горілка преміум-класу для тих, хто цінує чистий класичний горілчаний смак та беззаперечну якість. Її елітний статус підкреслюють:&nbsp;</p><ul><li>досконалий смак особливої м\'якості;</li><li>чистий та вишуканий аромат;</li><li>перемоги на сліпих закритих дегустаціях.</li></ul>', 35, 125, '656d90b1a610d.jpg', 1),
(24, 7, 'Absolut 1 л 40%', NULL, ' Прозорий, чистий', NULL, 16, 1, 40, 'Дуже м\'який, пряний, насичений, зі свіжим зерновим відтінком. Посмак тривалий, рівний й елегантний', NULL, NULL, '<p>Початок виробництва шведської горілки сягає XV століття — саме тоді скандинави почали виготовляти напій \"Branvin\", назва якого перекладається як «вогняне вино». Згодом дистильований спирт, названий горілкою, використовувався як медичний засіб, а в XVII столітті горілка стала національним шведським напоєм.</p><p>Історія <strong>Absolut</strong> налічує 130 років — перша пляшка <strong>Absolut</strong> була випущена в 1879 році. Саме тоді підприємець Ларс Олссон Сміт представив новий різновид горілки <strong>Absolut rent Bränvin</strong>, що в перекладі означає «абсолютно чиста горілка». Цей інноваційний продукт відрізнявся від подібних собі революційною технологією виробництва, в основі якої була безперервна дистиляція, що дало змогу якнайкраще очистити продукт від непотрібних домішок і зберегти його природний характер. Метод ректифікації використовується й до цього дня у виробництві горілки <strong>Absolut</strong>, а портрет Ларса Олссона Сміта, що заслужив ім\'я «Король горілки», прикрашає кожну пляшку <strong>Absolut</strong>.</p><p>Через 100 років після появи <strong>Absolut rent Bränvin</strong>, у 1979 році, шведською компанією V&amp;S Group було прийняте рішення про експорт горілки Absolut у США. На сьогодні <strong>Absolut</strong> продається на 126 ринках і є найбільшим алкогольним брендом, який займає лідерські позиції за кількістю продажу.</p><p>Кожна крапля горілки <strong>Absolut</strong> народжується в одному єдиному місці — містечку Ахус, розташованому на півдні Швеції. Починаючи з добірної озимої пшениці, закінчуючи водою з власного артезіанського джерела, Ахус надає всю необхідну сировину для мільйонів пляшок, що продаються в усьому світі.</p><p>Багатовіковий досвід показує, що озима пшениця дає змогу приготувати горілку найкращої якості. Саме тому для виробництва кожної пляшки <strong>Absolut</strong> використовується понад 1&nbsp;кг зерна. Усі представники сімейства <strong>Absolut</strong> дистилюються сотні разів, перш ніж вони зможуть називатися горілкою <strong>Absolut</strong>. Саме в цьому полягає суть процесу безперервної дистиляції, що входить в основу технології виробництва <strong>Absolut</strong>. Для всього сімейства <strong>Absolut</strong> використовуються тільки натуральні інгредієнти без додавання цукру.</p><p>Дистиляція горілки в одному місці з використанням місцевих інгредієнтів дає повний контроль на всіх етапах виробництва та забезпечує високі стандарти якості в кожній краплі горілки <strong>Absolut</strong>.</p>', 45, 490, '656d91495a625.jpg', 1),
(25, 8, 'Vilniaus Degtine Thorn Pink 0.7 л 37.5%', NULL, 'Рожевий', NULL, 17, 0.7, 37.5, 'Злегка обпікає', NULL, NULL, '<p><strong>Vilniaus Degtine</strong> спрямовує всю свою енергію на якість продуктів та послуг, які надає клієнтам. У 2000 році компанія ввела систему контролю якості ISO 9001 і відтоді дотримується її.<br>Відповідно до Регламенту (ЄС) № 1169/2011 цей спиртний напій не містить алергенів.</p>', 45, 289, '656d92258e534.jpg', 1),
(26, 8, 'Beefeater 0.7 л 40%', NULL, 'Прозорий', NULL, 18, 0.7, 40, 'Квітковий, м\'який, цедра', NULL, NULL, '<p>Рецепт джину Beefeater був створений у 1820 році Джеймсом Барроу, унаслідок чого світу було подаровано унікальний джин, з яскравим смаком та ароматом. Повний рецепт джину Beefeater відомий тільки 6 працівникам компанії, і відтоді жодного разу не змінювався. Beefeater — єдиний у світі джин, який досі виготовляється тільки в Лондоні.</p><p>Beefeater — дистильований джин. У процесі дистиляції інгредієнти поміщаються в чан разом зі спиртом і залишаються на 24 години, що дає змогу спирту просочитися запахом і смаком натуральних трав. Через певний проміжок часу спирт повільно, упродовж 8 годин, випаровується при невисокій температурі. У процесі виробництва джину Beefeater використовується тільки пшеничний спирт високого ступеня очищення, який не має запаху та дуже м\'який на смак.</p><p>Висока якість джину Beefeater, дивовижний смак і чудовий аромат є результатом ідеального поєднання ретельно відібраних інгредієнтів. Ялівець, що надає джину характерний аромат, закуповується в Італії, коріандр імпортується з Румунії та Болгарії, апельсинова та лимонна цедра привозиться з Південної Іспанії, корінь дягелю, що надає квітковий відтінок аромату джину, вирощується в Голландії й Бельгії.</p><p>Отже, джин Beefeater набуває свого унікального, чистого і підбадьорливого смаку із цитрусовими та фруктовими нотками.</p>', 45, 559, '656d9263906b9.jpg', 1),
(27, 9, 'Sierra Silver 0.7 л 38%', 'Текіла Сільвер', 'Прозорий', NULL, 19, 0.7, 38, 'Смак текіли дуже терпкий, глибокий, насичений і трохи солодкуватий, тривалий посмак бадьорить', NULL, NULL, '<p><strong>Текіла Sierra Silver</strong> — безбарвна молода ординарна текіла, яка містить 51% спирту блакитної агави, вирощеної в штаті Халіско, біля його столиці — міста Гвадалахари, на висоті 1500 метрів над рівнем моря. Тільки професіонали з покоління в покоління працюють на полях агави і піклуються про весь процес від посадки до збирання врожаю. Готують агаву 12 годин під високим тиском і ще 12 годин при низькій температурі, після чого слід м\'який віджимання соку.</p>', 45, 499, '656d934ae2397.jpg', 1),
(28, 9, 'Sierra Reposado 0.7 л 38%', 'Репосадо', 'Текіла бурштинового кольору з яскравими жовтими відблисками', NULL, 19, 0.7, 38, 'Смак текіли м\'який, багатий. Чудовий посмак, тривалий і незабутній', NULL, NULL, '<p><strong>Текіла Sierra</strong> — це особливий напій, який виробляють із перестиглих, що буквально стікають соком, плодів блакитної агави, вирощеної в штаті Халіско, біля його столиці — міста Гвадалахари, на висоті 1500 метрів над рівнем моря. Тільки професіонали з покоління в покоління працюють на полях агави та піклуються про весь процес від саджання до збирання врожаю. Готують агаву 12 годин під високим тиском і ще 12 годин за низької температури, після чого відбувається м\'яке віджимання соку.</p>', 35, 499, '656d938cd7821.jpg', 1),
(29, 10, 'Старий Кахеті 3 роки витримки 0.5 л 40%', 'Бренді', 'Темний бурштиновий', NULL, NULL, 0.5, 40, 'Виноградний, гармонійний, м\'який, з приємною солодкістю', 2, 3, '<p>Історія походження компанії \"Кахетинське традиційне виноробство\" пов\'язана з виноробом на ім\'я Антимоз Чхаїдзе, який у 80-х роках XIX століття в гурійському селі Аскана заснував один із найкращих винних погребів того часу, звідки й бере початок унікальний сорт вина — \"Чхавері\". Це вино прославило винороба не тільки в Грузії, але й в інших країнах Європи. На основі старовинних сімейних традицій у 2001 році була заснована компанія під назвою \"Кахетинське традиційне виноробство\", яка зберігає і продовжує традиції, що йдуть глибоко в старовину та фактично мають довгу 800-річну історію.</p><p>У Грузії є місцеві технології виробництва алкогольних напоїв, найбільш поширена серед яких кахетинська. Кахетія розташована в південно-східній частині країни. Завдяки особливому клімату та ґрунту Кахетія посідає виняткове місце у виноробній галузі. Тому виробництво вина в цьому регіоні найбільш широкомасштабне.</p><p>Суть кахетинського методу виробництва вина полягає в тому, що після віджимання винограду для зберігання вина й мачарі використовується глиняний посуд — квеврі. Його виготовляють з обробленої та випаленої глини. Квеврі закопують у землю, де найкращі умови для підтримування температури вина. Крім того, такий посуд надає вину корисні властивості й особливий смак. Квеврі — невіддільна частина грузинського виноробства. Глина та кераміка з давніх часів використовувалася в Грузії.</p>', 45, 239, '656db46c115ee.jpg', 1),
(30, 10, 'Aznauri 3 роки витримки 0.7 л 40%', 'Коньяк', 'Насичений темно-золотистий', NULL, NULL, 0.7, 40, 'Квітковий, фруктовий', NULL, 3, '<p>Компанія Global Beverage Trade була заснована в 1996 році. Основний напрям діяльності — виробництво та дистрибуція алкогольної продукції власних торговельних марок. На території України компанія володіє трьома власними логістичними центрами, загальна площа яких становить понад 11 тис. м². Річний обіг продукції компанії в середньому становить понад 20 мільйонів пляшок.</p>', 35, 209, '656db4c343046.jpg', 1),
(31, 11, 'Ghost 0.7 л 60%', NULL, 'Зелений', NULL, 22, 0.7, 60, 'Солодкувато-пряний', NULL, NULL, '<p>ТОВ Горобина, створене в 2001 році на базі Сумського лікеро-горілчаного заводу, у своїй діяльності продовжує найстаріші традиції сумських майстрів. Досвід, накопичений десятиліттями, дає змогу підприємству забезпечувати незмінно високу якість продукції, яка базується на використанні виключно натуральної сировини. \"«Традиції, якість, натуральність» – девіз, якому Горобина незмінно вірна упродовж багатьох років.</p><p>З кінця 2014 року вся продукція виготовляється на базі структурованої води (більш відомої як \"тала вода\") і застосовується 9-ступенева система фільтрації водно-спиртової суміші з використанням напівдорогоцінних каменів і мінералів. Такий підхід до справи дав нагоду підняти якість випущених напоїв на найвищий рівень!</p>', 20, 399, '656db56a09b07.jpg', 1),
(32, 12, 'Krombacher Pils світле фільтроване 4.8% 5 л', 'Фільтроване', 'Світле', NULL, 23, 5, 4.8, 'Пиво', NULL, NULL, '<p><strong>Krombacher (Кромбахер)</strong> — пиво №1 преміум сегменту в Німеччині.</p><p>Найбільша німецька пивоварня <strong>Krombacher</strong> розташована в мальовничому містечку Кромбі в Північній Рейн-Вестфалії. Пивоварний завод <strong>Krombacher</strong> з дня свого заснування до сьогодні розташований у володінні сімейства <strong>Schadeberg</strong>. Під час варіння пива Krombacher суворо дотримується Закону про чистоту пива <strong>1516</strong> року, згідно з яким у пиві можливі тільки чотири натуральні компоненти: ячмінь, хміль, дріжджі та вода.</p><p>Оригінальна рецептура пива <strong>Krombacher</strong> не зазнала змін із моменту заснування пивоварні. Сімейство <strong>Schadeberg</strong> стежать за дотриманням стандартів якості <strong>Krombacher</strong> так само суворо, як і багато років тому. З Кромбахер <strong>Pils</strong> Ви насолоджуєтеся характерним тонким смаком гіркоти та повним смаковим ароматом — справжнє пиво преміум класу з вмістом алкоголю 4.8%. Сучасні виробничі процеси покращують традиційний рецепт із використанням натуральних продуктів, який був використаний упродовж декількох поколінь, щоб створити унікальний напій — Кромбахер Pils.</p><p><strong>Термін придатності:</strong> 360 днів</p>', 10, 640, '656db692dd30d.jpg', 1),
(33, 12, 'Упаковка Kronenbourg 1664 Blanc світле 4.8% 0.33 л x 4 шт.', 'Фільтроване', ' Світле', NULL, 24, 0.33, 4.8, 'Пиво', NULL, NULL, '<p><strong>Kronenbourg 1664</strong> – це французьке пиво №1 у світі — бренд представлений більш ніж в 70 країнах. Особливо успішний цей напій у Європі, де входить до числа лідерів серед преміальних брендів.</p><p><strong>Kronenbourg 1664 Blanc</strong> — пиво із фруктовими нотками, яке виробляється також за оригінальною рецептурою, розробленою у Франції. Має м\'який солодовий смак з тонким ароматом цитрусових і апельсинової цедри. Ця комбінація доповнює пряно-фруктовий присмак, який змінюється солодкуватими, ледь відчутними пшеничними відтінками.</p><p>Преміальне пиво <strong>Kronenbourg 1664</strong> – офіційний посол французького стилю життя. З моменту свого створення бренд символізує перевагу й вишуканість у кожній деталі: французька рецептура приготування пива, сировина високої якості, витончений смак і аромат. Пляшка з великим рельєфним тисненням «1664» гармонійно доповнює преміальний імідж бренда, а за своєю формою вона нагадує Ейфелеву вежу – один їх найвідоміших символів Франції.</p>', 10, 90, '656db722d7f48.jpg', 1);

